$(document).ready(function() {
  $(".li1").mouseover(function() {
    $(this).css("transition","1.5s");
    $(this).addClass("spin");
  });
  $(".li1").mouseout(function() {
    setTimeout(function() {
      $(".li1").css("transition","0s");
      $(".li1").removeClass("spin");
    }, 1500);
  });
});

